import { Component, OnInit } from '@angular/core';
import { TaskService } from '../../services/task.service';
import { TaskItem, TaskItemStatus } from '../../models/task-item.model';

@Component({
  selector: 'app-task-list',
  templateUrl: './task-list.component.html',
  styleUrls: ['./task-list.component.css']
})
export class TaskListComponent implements OnInit {
  tasks: TaskItem[] = [];
  filteredTasks: TaskItem[] = [];
  selectedStatus: string = 'all';
  searchTerm: string = '';
  isLoading: boolean = false;
  showModal: boolean = false;
  editingTask: TaskItem | null = null;
 errorMessages: string[] = [];
  TaskItemStatus = TaskItemStatus;

  taskForm = {
    title: '',
    description: '',
    dueDate: '',
    status: TaskItemStatus.Pending
  };

  constructor(private taskService: TaskService) {}

  ngOnInit(): void {
    this.loadTasks();
  }

  loadTasks(status?: TaskItemStatus): void {
    this.isLoading = true;
    this.taskService.getTasksByStatus(status).subscribe({
      next: (data) => {
        this.tasks = data;
        this.applyFilters();
        this.isLoading = false;
      },
      error: (error) => {
        console.error('Görevler yüklenirken hata:', error);
        this.isLoading = false;
      }
    });
  }

  applyFilters(): void {
    let filtered = [...this.tasks];

    if (this.selectedStatus !== 'all') {
      filtered = filtered.filter(task =>
        task.status === parseInt(this.selectedStatus)
      );
    }

    if (this.searchTerm) {
      const search = this.searchTerm.toLowerCase();
      filtered = filtered.filter(task =>
        task.title.toLowerCase().includes(search) ||
        (task.description && task.description.toLowerCase().includes(search))
      );
    }

    this.filteredTasks = filtered;
  }

  onStatusFilterChange(event: any): void {
    this.selectedStatus = event.target.value;
    this.applyFilters();
  }

  onSearchChange(): void {
    this.applyFilters();
  }

  openCreateModal(): void {
    this.editingTask = null;
    this.taskForm = {
      title: '',
      description: '',
      dueDate: '',
      status: TaskItemStatus.Pending
    };
    this.showModal = true;
  }

  openEditModal(task: TaskItem): void {
    this.editingTask = task;
    this.taskForm = {
      title: task.title,
      description: task.description || '',
      dueDate: new Date(task.dueDate).toISOString().split('T')[0],
      status: task.status
    };
    this.showModal = true;
  }

  closeModal(): void {
    this.showModal = false;
    this.editingTask = null;
  }

  saveTask(): void {

    if (!this.taskForm.title || !this.taskForm.dueDate) {
      alert('Lütfen tüm zorunlu alanları doldurun');
      return;
    }
      this.errorMessages=[];


    const command = {
      title: this.taskForm.title,
      description: this.taskForm.description,
      dueDate: new Date(this.taskForm.dueDate),
      status: this.taskForm.status
    };

    if (this.editingTask) {
      const updateCommand = { ...command, id: this.editingTask.id };
      this.taskService.updateTask(this.editingTask.id, updateCommand).subscribe({
        next: () => {
          this.loadTasks();
          this.closeModal();
        },
        error: (error) => console.error('Güncelleme hatası:', error)
      });
    } else {
      this.taskService.createTask(command).subscribe({
        next: () => {
          this.loadTasks();
          this.closeModal();
        },
        error: (error) => {
           console.error('Oluşturma hatası:', error);

    // 🔹 Eğer API FluentValidation hataları döndüyse
    if (error?.error?.errors && Array.isArray(error.error.errors)) {
      console.log('FluentValidation Hataları:');
      error.error.errors.forEach((e: any) => {
        console.log(`Alan: ${e.PropertyName} -> Hata: ${e.ErrorMessage}`);
      });
    }
    // 🔹 API sade bir string döndüyse (örnek: "Bad Request")
    else if (typeof error?.error === 'string') {
      console.log('API Hatası:', error.error);
    }
    // 🔹 Genel fallback
    else {
      console.log('Beklenmeyen bir hata oluştu.');
    }
        }
      });
    }
  }

  deleteTask(id: string): void {
    if (confirm('Bu görevi silmek istediğinizden emin misiniz?')) {
      this.taskService.deleteTask(id).subscribe({
        next: () => this.loadTasks(),
        error: (error) => console.error('Silme hatası:', error)
      });
    }
  }

  updateTaskStatus(task: TaskItem, event: any): void {
    const newStatus = parseInt(event.target.value);
    const command = {
      id: task.id,
      title: task.title,
      description: task.description,
      dueDate: task.dueDate,
      status: newStatus
    };

    this.taskService.updateTask(task.id, command).subscribe({
      next: () => this.loadTasks(),
      error: (error) => console.error('Durum güncelleme hatası:', error)
    });
  }

  getStatusText(status: TaskItemStatus): string {
    switch (status) {
      case TaskItemStatus.Pending: return 'Beklemede';
      case TaskItemStatus.InProgress: return 'Devam Ediyor';
      case TaskItemStatus.Completed: return 'Tamamlandı';
      default: return '';
    }
  }

  getStatusClass(status: TaskItemStatus): string {
    switch (status) {
      case TaskItemStatus.Pending: return 'status-pending';
      case TaskItemStatus.InProgress: return 'status-in-progress';
      case TaskItemStatus.Completed: return 'status-completed';
      default: return '';
    }
  }

  get pendingCount(): number {
    return this.tasks.filter(t => t.status === TaskItemStatus.Pending).length;
  }

  get inProgressCount(): number {
    return this.tasks.filter(t => t.status === TaskItemStatus.InProgress).length;
  }

  get completedCount(): number {
    return this.tasks.filter(t => t.status === TaskItemStatus.Completed).length;
  }
}
<div class="task-management-container">
  <!-- Header -->
  <div class="header">
    <div>
      <h1>Görev Yönetimi</h1>
      <p>Görevlerinizi organize edin ve takip edin</p>
    </div>
  </div>

  <!-- Filters and Search -->
  <div class="filters-section">
    <div class="filters-left">
      <select [(ngModel)]="selectedStatus" (change)="onStatusFilterChange($event)" class="filter-select">
        <option value="all">Tüm Görevler</option>
        <option [value]="TaskItemStatus.Pending">Beklemede</option>
        <option [value]="TaskItemStatus.InProgress">Devam Ediyor</option>
        <option [value]="TaskItemStatus.Completed">Tamamlandı</option>
      </select>
    </div>

    <div class="filters-right">
      <input
        type="text"
        [(ngModel)]="searchTerm"
        (input)="onSearchChange()"
        placeholder="Görev ara..."
        class="search-input"
      />
      <button (click)="openCreateModal()" class="btn-primary">
        ➕ Yeni Görev
      </button>
    </div>
  </div>

  <!-- Stats -->
  <div class="stats-grid">
    <div class="stat-card">
      <div class="stat-label">Toplam Görev</div>
      <div class="stat-value">{{ tasks.length }}</div>
    </div>
    <div class="stat-card stat-pending">
      <div class="stat-label">Beklemede</div>
      <div class="stat-value">{{ pendingCount }}</div>
    </div>
    <div class="stat-card stat-progress">
      <div class="stat-label">Devam Ediyor</div>
      <div class="stat-value">{{ inProgressCount }}</div>
      </div>
    <div class="stat-card stat-completed">
      <div class="stat-label">Tamamlandı</div>
      <div class="stat-value">{{ completedCount }}</div>
    </div>
  </div>

  <!-- Loading -->
  <div *ngIf="isLoading" class="loading">
    <div class="spinner"></div>
    <p>Yükleniyor...</p>
  </div>

  <!-- Task Grid -->
  <div class="task-grid" *ngIf="!isLoading">
    <div *ngFor="let task of filteredTasks" class="task-card">
      <div class="task-header">
        <h3>{{ task.title }}</h3>
        <div class="task-actions">
          <button (click)="openEditModal(task)" class="btn-icon btn-edit" title="Düzenle">
            ✏️
          </button>
          <button (click)="deleteTask(task.id)" class="btn-icon btn-delete" title="Sil">
            🗑️
          </button>
        </div>
      </div>

      <p class="task-description">{{ task.description }}</p>

      <div class="task-badges">
        <span [class]="'badge ' + getStatusClass(task.status)">
          {{ getStatusText(task.status) }}
        </span>
      </div>

      <div class="task-meta">
        <div class="meta-item">
          📅 Bitiş: {{ task.dueDate | date:'dd.MM.yyyy' }}
        </div>
        <div class="meta-item">
          🕐 Oluşturma: {{ task.createdDate | date:'dd.MM.yyyy' }}
        </div>
      </div>

      <div class="task-status-select">
        <select
          [value]="task.status"
          (change)="updateTaskStatus(task, $event)"
          class="status-dropdown"
        >
          <option [value]="TaskItemStatus.Pending">Beklemede</option>
          <option [value]="TaskItemStatus.InProgress">Devam Ediyor</option>
          <option [value]="TaskItemStatus.Completed">Tamamlandı</option>
        </select>
      </div>
    </div>
  </div>

  <!-- Empty State -->
  <div *ngIf="!isLoading && filteredTasks.length === 0" class="empty-state">
    <div class="empty-icon">📋</div>
    <p>Görev bulunamadı</p>
  </div>

  <!-- Modal -->
  <div *ngIf="showModal" class="modal-overlay" (click)="closeModal()">
    <div class="modal-content" (click)="$event.stopPropagation()">
      <div class="modal-header">
        <h2>{{ editingTask ? 'Görevi Düzenle' : 'Yeni Görev Oluştur' }}</h2>
        <button (click)="closeModal()" class="btn-close">✕</button>
      </div>

      <div class="modal-body">
            <!-- Genel Hata Mesajları (validationErrors.general veya errorMessages) -->
            <div *ngIf="errorMessages.length > 0" class="alert alert-danger">
                <p *ngFor="let msg of errorMessages">{{ msg }}</p>
            </div>

        <div class="form-group">
          <label>Görev Başlığı <span class="required">*</span></label>
          <input
            type="text"
            [(ngModel)]="taskForm.title"
            placeholder="Görev başlığını girin"
            class="form-control"
          />
            <!-- 🔴 Başlık (Title) alanı için spesifik hata gösterimi -->
            <div *ngIf="validationErrors.Title" class="validation-error">
                <span *ngFor="let error of validationErrors.Title">{{ error }}</span>
            </div>
        </div>

        <div class="form-group">
          <label>Açıklama</label>
          <textarea
            [(ngModel)]="taskForm.description"
            rows="4"
            placeholder="Görev açıklamasını girin"
            class="form-control"
          ></textarea>
            <!-- 🔴 Açıklama (Description) alanı için spesifik hata gösterimi (Varsa) -->
            <div *ngIf="validationErrors.Description" class="validation-error">
                <span *ngFor="let error of validationErrors.Description">{{ error }}</span>
            </div>
        </div>

        <div class="form-row">
          <div class="form-group">
            <label>Durum</label>
            <select [(ngModel)]="taskForm.status" class="form-control">
              <option [value]="TaskItemStatus.Pending">Beklemede</option>
              <option [value]="TaskItemStatus.InProgress">Devam Ediyor</option>
              <option [value]="TaskItemStatus.Completed">Tamamlandı</option>
            </select>
          </div>

          <div class="form-group">
            <label>Bitiş Tarihi <span class="required">*</span></label>
            <input
              type="date"
              [(ngModel)]="taskForm.dueDate"
              class="form-control"
            />
            <!-- 🔴 Bitiş Tarihi (DueDate) alanı için spesifik hata gösterimi -->
            <div *ngIf="validationErrors.DueDate" class="validation-error">
                <span *ngFor="let error of validationErrors.DueDate">{{ error }}</span>
            </div>
          </div>
        </div>
      </div>

      <div class="modal-footer">
        <button (click)="closeModal()" class="btn-secondary">İptal</button>
        <button (click)="saveTask()" class="btn-primary">
          {{ editingTask ? 'Güncelle' : 'Oluştur' }}
        </button>
      </div>
    </div>
  </div>
</div>
